import { useState } from "react";

export function useFormFields(initialState) {
  const [fields, setValues] = useState(initialState);

  return [
    fields,
    function(event) {
      setValues({
        ...fields,
        [event.target.id]: event.target.value
      });
    },
    function(values) {
      setValues(values)
    }
  ];
}

 export function setUser(user){
   return{
     type:'SET_USER',
     payload: user
   }
 }
